import groovy.json.JsonOutput
def primary = null;
def second = null;
def map = new HashMap();
if ( telephoneNumber != null ) {
    map.put('value', telephoneNumber);
}
map.put('primary', true);
map.put('type', 'work');
primary = JsonOutput.toJson(map);
log.warn("Primary Phone {}", primary);

map = new HashMap();
if ( mobileNumber != null ) {
    map.put('value', mobileNumber);
}
map.put('primary', false);
map.put('type', 'mobile');
second = JsonOutput.toJson(map);
log.warn("Mobile Phone {}", second);


return [primary, second];
